// src/components/ChatWindow.jsx
import React, { useState } from "react";

const ChatWindow = ({ chat, goBack }) => {
  // Example messages
  const [messages, setMessages] = useState([
    { sender: "them", text: `Hey, I'm ${chat.name}.` },
    { sender: "me", text: "Hello! Good to chat with you." },
  ]);

  const [newMessage, setNewMessage] = useState("");

  const handleSend = () => {
    if (newMessage.trim() !== "") {
      setMessages((prev) => [...prev, { sender: "me", text: newMessage }]);
      setNewMessage("");
    }
  };

  return (
    <div>
      {/* Back button */}
      <button
        onClick={goBack}
        style={{
          background: "none",
          border: "none",
          color: "#fff",
          cursor: "pointer",
          marginBottom: "8px",
        }}
      >
        &larr; Back
      </button>

      {/* Chat messages */}
      <div
        style={{
          maxHeight: "200px",
          overflowY: "auto",
          border: "1px solid #666",
          marginBottom: "8px",
          padding: "8px",
          color: "#fff",
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              textAlign: msg.sender === "me" ? "right" : "left",
              margin: "6px 0",
            }}
          >
            <span>{msg.text}</span>
          </div>
        ))}
      </div>

      {/* Input for new message */}
      <div style={{ display: "flex", gap: "6px" }}>
        <input
          type="text"
          value={newMessage}
          placeholder="Type a message..."
          onChange={(e) => setNewMessage(e.target.value)}
          style={{ flex: 1, padding: "6px", borderRadius: "4px" }}
        />
        <button
          onClick={handleSend}
          style={{ backgroundColor: "#DEDBDB", border: "none", cursor: "pointer" }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
