import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import './ChatListPage.css';

const dummyChats = [
  { id: 1, name: 'Friend 1' },
  { id: 2, name: 'Friend 2' },
  { id: 3, name: 'Seller 1' },
];

const ChatListPage = () => {
  const [selectedChat, setSelectedChat] = useState(null);

  return (
    <div className="chats-container">
      <div className="chats-wrapper">
        {/* Left Panel: Chat List */}
        <div className="chats-left-panel">
          <header className="chats-left-header">
            {/* Arrow-only back button in the top-left corner */}
            <Link to="/dashboard" className="back-link arrow-only">
              <FontAwesomeIcon icon={faArrowLeft} />
            </Link>
            {/* Centered title */}
            <h2 className="chats-title">Chat</h2>
          </header>

          <ul className="chat-list">
            {dummyChats.map((chat) => (
              <li
                key={chat.id}
                className={`chat-item ${selectedChat === chat.id ? 'active' : ''}`}
                onClick={() => setSelectedChat(chat.id)}
              >
                {chat.name}
              </li>
            ))}
          </ul>
        </div>
        
        {/* Right Panel: Chat Messages */}
        <div className="chats-right-panel">
          {selectedChat ? (
            <div className="chat-content">
              <header className="chats-right-header">
                <span className="chat-contact-name">
                  {dummyChats.find((chat) => chat.id === selectedChat)?.name}
                </span>
              </header>
              <div className="chat-messages">
                <p className="message-bubble theirs">Hello!</p>
                <p className="message-bubble mine">Hi there!</p>
              </div>
              <div className="chats-input-area">
                <input type="text" placeholder="Type a message..." />
                <button>Send</button>
              </div>
            </div>
          ) : (
            /* Centered placeholder message */
            <div className="chat-placeholder">Start Messaging</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatListPage;
