import { useState } from "react";

const ChatWindow = ({ chat, goBack }) => {
  const [messages, setMessages] = useState([
    { sender: "them", text: "Hey!" },
    { sender: "me", text: "Hello!" },
  ]);

  const [newMessage, setNewMessage] = useState("");

  const sendMessage = () => {
    if (newMessage.trim()) {
      setMessages([...messages, { sender: "me", text: newMessage }]);
      setNewMessage("");
    }
  };

  return (
    <div className="p-4 bg-gray-900 text-white rounded-lg">
      <button onClick={goBack} className="mb-2 text-blue-500">â¬… Back</button>
      <h2 className="text-lg font-semibold">{chat.name}</h2>
      <div className="h-40 overflow-y-auto my-2 border-b border-gray-700">
        {messages.map((msg, index) => (
          <p key={index} className={msg.sender === "me" ? "text-right" : ""}>
            {msg.text}
          </p>
        ))}
      </div>
      <div className="flex mt-2">
        <input
          type="text"
          className="flex-1 p-2 rounded bg-gray-800"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={sendMessage} className="ml-2 bg-blue-500 px-3 py-1 rounded">
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
