import { useState } from "react";
import ChatWindow from "./ChatWindow";

const chats = [
  { id: 1, name: "Seller 1" },
  { id: 2, name: "Friend A" },
  { id: 3, name: "Friend B" },
];

const ChatList = () => {
  const [selectedChat, setSelectedChat] = useState(null);

  return (
    <div className="absolute top-10 right-0 bg-gray-800 p-4 w-64 rounded-lg shadow-lg">
      {selectedChat ? (
        <ChatWindow chat={selectedChat} goBack={() => setSelectedChat(null)} />
      ) : (
        <ul>
          {chats.map((chat) => (
            <li
              key={chat.id}
              className="p-2 text-white cursor-pointer hover:bg-gray-700"
              onClick={() => setSelectedChat(chat)}
            >
              {chat.name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ChatList;
