import React, { useState } from "react";
import ChatWindow from "./ChatWindow";

/**
 * Dummy chat data for demonstration.
 * In a real app, you’d fetch this from your backend or context.
 */
const DUMMY_CHATS = [
  { id: 1, name: "Seller 1" },
  { id: 2, name: "Friend A" },
  { id: 3, name: "Friend B" },
];

const ChatList = ({ onClose }) => {
  const [selectedChat, setSelectedChat] = useState(null);

  const handleSelectChat = (chat) => {
    setSelectedChat(chat);
  };

  const handleGoBack = () => {
    setSelectedChat(null);
  };

  return (
    <div
      style={{
        position: "absolute",
        top: "40px",
        right: 0,
        backgroundColor: "#444",
        width: "300px",
        borderRadius: "8px",
        padding: "10px",
        zIndex: 9999,
      }}
    >
      {selectedChat ? (
        // If a chat is selected, show the ChatWindow
        <ChatWindow chat={selectedChat} goBack={handleGoBack} />
      ) : (
        // Otherwise, show the list of chats
        <>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "10px",
              color: "#fff",
            }}
          >
            <h4 style={{ margin: 0 }}>Chats</h4>
            <button
              onClick={onClose}
              style={{
                background: "none",
                border: "none",
                color: "#fff",
                cursor: "pointer",
                fontSize: "16px",
              }}
            >
              X
            </button>
          </div>

          <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
            {DUMMY_CHATS.map((chat) => (
              <li
                key={chat.id}
                onClick={() => handleSelectChat(chat)}
                style={{
                  padding: "8px",
                  cursor: "pointer",
                  borderBottom: "1px solid #666",
                  color: "#fff",
                }}
              >
                {chat.name}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default ChatList;
