import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faArrowLeft,
  faEllipsisV,
  faDownload,
} from '@fortawesome/free-solid-svg-icons';
import './ChatListPage.css';

const initialChats = [
  { id: 1, name: 'Friend 1' },
  { id: 2, name: 'Friend 2' },
  { id: 3, name: 'Seller 1' },
];

const ChatListPage = () => {
  // Store chat list in state so we can delete a chat
  const [chatList, setChatList] = useState(initialChats);

  // Track which chat is selected
  const [selectedChat, setSelectedChat] = useState(null);

  // Example messages (with timestamps, stored by chat ID)
  const [messages, setMessages] = useState({
    1: [
      { sender: 'theirs', text: 'Hello!', time: '10:00 AM', type: 'text' },
      { sender: 'mine', text: 'Hi there!', time: '10:01 AM', type: 'text' },
    ],
    2: [],
    3: [],
  });

  // Track what the user is typing
  const [typedMessage, setTypedMessage] = useState('');

  // For image uploads
  const fileInputRef = useRef(null);

  // For image modal (zoomed image)
  const [modalImage, setModalImage] = useState(null);

  // For the 3-dot menu
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);

  // Generate a timestamp (e.g., 10:02 AM)
  const getTimeStamp = () => {
    return new Date().toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Handle selecting a chat
  const handleSelectChat = (chatId) => {
    setSelectedChat(chatId);
    setTypedMessage('');
    setMenuOpen(false); // close menu if switching chats
  };

  // Handle sending a text message
  const handleSend = () => {
    if (!typedMessage.trim() || !selectedChat) return;
    const timeStamp = getTimeStamp();

    setMessages((prev) => ({
      ...prev,
      [selectedChat]: [
        ...(prev[selectedChat] || []),
        {
          sender: 'mine',
          text: typedMessage,
          time: timeStamp,
          type: 'text',
        },
      ],
    }));

    setTypedMessage('');
  };

  // Handle clicking the "Upload Image" button
  const handleUploadClick = () => {
    if (!selectedChat) return;
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  // Handle when a file is selected
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file || !selectedChat) return;

    const reader = new FileReader();
    reader.onload = () => {
      const timeStamp = getTimeStamp();
      setMessages((prev) => ({
        ...prev,
        [selectedChat]: [
          ...(prev[selectedChat] || []),
          {
            sender: 'mine',
            src: reader.result, // base64 data URL
            time: timeStamp,
            type: 'image',
          },
        ],
      }));
    };
    reader.readAsDataURL(file);

    // Reset file input so same file can be re-selected if desired
    e.target.value = '';
  };

  // Open image modal with a larger view
  const openImageModal = (src) => {
    setModalImage(src);
  };

  // Close image modal
  const closeImageModal = () => {
    setModalImage(null);
  };

  // Clear all messages in the selected chat
  const handleClearChat = () => {
    if (!selectedChat) return;
    setMessages((prev) => ({
      ...prev,
      [selectedChat]: [],
    }));
    setMenuOpen(false);
  };

  // Delete the selected chat entirely
  const handleDeleteChat = () => {
    if (!selectedChat) return;
    // Remove from chatList
    setChatList((prevList) => prevList.filter((chat) => chat.id !== selectedChat));
    // Remove messages
    setMessages((prev) => {
      const newMessages = { ...prev };
      delete newMessages[selectedChat];
      return newMessages;
    });
    // Deselect chat
    setSelectedChat(null);
    setMenuOpen(false);
  };

  // Toggle the 3-dot menu
  const toggleMenu = () => {
    setMenuOpen((prev) => !prev);
  };

  // Close the menu if clicked outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="chats-container">
      <div className="chats-wrapper">
        {/* Left Panel: Chat List */}
        <div className="chats-left-panel">
          <header className="chats-left-header">
            {/* Arrow-only back button in the top-left corner */}
            <Link to="/dashboard" className="back-link arrow-only">
              <FontAwesomeIcon icon={faArrowLeft} />
            </Link>
            {/* Centered title */}
            <h2 className="chats-title">Chat</h2>
          </header>

          <ul className="chat-list">
            {chatList.map((chat) => (
              <li
                key={chat.id}
                className={`chat-item ${selectedChat === chat.id ? 'active' : ''}`}
                onClick={() => handleSelectChat(chat.id)}
              >
                {chat.name}
              </li>
            ))}
          </ul>
        </div>

        {/* Right Panel: Chat Messages */}
        <div className="chats-right-panel">
          {selectedChat ? (
            <div className="chat-content">
              <header className="chats-right-header">
                <span className="chat-contact-name">
                  {chatList.find((chat) => chat.id === selectedChat)?.name}
                </span>
                <div className="chat-header-actions" ref={menuRef}>
                  <FontAwesomeIcon
                    icon={faEllipsisV}
                    className="ellipsis-icon"
                    onClick={toggleMenu}
                  />
                  {menuOpen && (
                    <div className="chat-menu-dropdown">
                      <div className="dropdown-item" onClick={handleClearChat}>
                        Clear Chat
                      </div>
                      <div className="dropdown-item" onClick={handleDeleteChat}>
                        Delete Chat
                      </div>
                    </div>
                  )}
                </div>
              </header>
              <div className="chat-messages">
                {/* Render messages for the selected chat */}
                {messages[selectedChat]?.map((msg, index) => {
                  if (msg.type === 'text') {
                    // Text message
                    return (
                      <div key={index} className={`message-bubble ${msg.sender}`}>
                        <div className="message-text">{msg.text}</div>
                        <div className="message-time">{msg.time}</div>
                      </div>
                    );
                  } else {
                    // Image message
                    return (
                      <div key={index} className={`message-bubble ${msg.sender}`}>
                        <img
                          src={msg.src}
                          alt="sent"
                          className="message-image"
                          onClick={() => openImageModal(msg.src)}
                        />
                        <div className="message-time">{msg.time}</div>
                      </div>
                    );
                  }
                })}
              </div>
              <div className="chats-input-area">
                <input
                  type="text"
                  placeholder="Type a message..."
                  value={typedMessage}
                  onChange={(e) => setTypedMessage(e.target.value)}
                />
                <button onClick={handleSend}>Send</button>
                {/* Hidden file input for image uploading */}
                <input
                  type="file"
                  accept="image/*"
                  ref={fileInputRef}
                  style={{ display: 'none' }}
                  onChange={handleFileChange}
                />
                <button onClick={handleUploadClick}>Upload Image</button>
              </div>
            </div>
          ) : (
            /* Centered placeholder message */
            <div className="chat-placeholder">Start Messaging</div>
          )}
        </div>
      </div>

      {/* Image Modal for enlarged preview */}
      {modalImage && (
        <div className="image-modal" onClick={closeImageModal}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            {/* Close icon in the top-right corner */}
            <span className="close-modal-icon" onClick={closeImageModal}>
              &times;
            </span>
            <img src={modalImage} alt="Enlarged" className="modal-image" />
            {/* Download icon link (force download attribute) */}
            <a href={modalImage} download="image.png" className="download-link">
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatListPage;
