import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faArrowLeft,
  faEllipsisV,
  faEllipsisH,
  faDownload,
  faPaperclip,
  faPaperPlane,
  faFilePdf,
} from '@fortawesome/free-solid-svg-icons';
import './ChatListPage.css';

const initialChats = [
  { id: 1, name: 'Friend 1' },
  { id: 2, name: 'Friend 2' },
  { id: 3, name: 'Seller 1' },
];

const ChatListPage = () => {
  // Chat list state
  const [chatList, setChatList] = useState(initialChats);
  // Selected chat ID
  const [selectedChat, setSelectedChat] = useState(null);
  // Messages stored by chat id
  const [messages, setMessages] = useState({
    1: [
      { sender: 'theirs', text: 'Hello!', time: '10:00 AM', type: 'text' },
      { sender: 'mine', text: 'Hi there!', time: '10:01 AM', type: 'text' },
    ],
    2: [],
    3: [],
  });
  // For typing a new message
  const [typedMessage, setTypedMessage] = useState('');
  // For file uploads
  const fileInputRef = useRef(null);
  // For image modal preview
  const [modalImage, setModalImage] = useState(null);
  // For PDF modal preview
  const [pdfModal, setPdfModal] = useState({ open: false, src: '', fileName: '' });
  // For chat header menu (Clear/Delete chat)
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);
  // For storing the index of message whose options dropdown is open
  const [openMsgOptions, setOpenMsgOptions] = useState(null);
  // For reply feature; store the message being replied to
  const [replyMsg, setReplyMsg] = useState(null);

  // Generate a timestamp (e.g., 10:02 AM)
  const getTimeStamp = () => {
    return new Date().toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Handle selecting a chat from the left panel
  const handleSelectChat = (chatId) => {
    setSelectedChat(chatId);
    setTypedMessage('');
    setMenuOpen(false);
    setReplyMsg(null);
  };

  // Handle sending a text message (with reply info if set)
  const handleSend = () => {
    if (!typedMessage.trim() || !selectedChat) return;
    const timeStamp = getTimeStamp();
    // If replying, prepend a reference to the replied message text
    const replyReference = replyMsg ? `Replying to: "${replyMsg.text}"\n` : '';
    const newText = replyReference + typedMessage;
    setMessages((prev) => ({
      ...prev,
      [selectedChat]: [
        ...(prev[selectedChat] || []),
        {
          sender: 'mine',
          text: newText,
          time: timeStamp,
          type: 'text',
          replyTo: replyMsg, // you can store full reply object if desired
        },
      ],
    }));
    setTypedMessage('');
    setReplyMsg(null);
  };

  // Handle file upload click
  const handleUploadClick = () => {
    if (!selectedChat) return;
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  // Handle file input change (for images, PDFs, or other files)
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file || !selectedChat) return;

    const timeStamp = getTimeStamp();
    const fileName = file.name;
    const fileType = file.type.toLowerCase();

    const reader = new FileReader();
    reader.onload = () => {
      if (fileType.startsWith('image/')) {
        setMessages((prev) => ({
          ...prev,
          [selectedChat]: [
            ...(prev[selectedChat] || []),
            {
              sender: 'mine',
              src: reader.result,
              time: timeStamp,
              type: 'image',
            },
          ],
        }));
      } else if (
        fileType === 'application/pdf' ||
        fileName.toLowerCase().endsWith('.pdf')
      ) {
        setMessages((prev) => ({
          ...prev,
          [selectedChat]: [
            ...(prev[selectedChat] || []),
            {
              sender: 'mine',
              src: reader.result,
              time: timeStamp,
              type: 'pdf',
              fileName: fileName,
            },
          ],
        }));
      } else {
        setMessages((prev) => ({
          ...prev,
          [selectedChat]: [
            ...(prev[selectedChat] || []),
            {
              sender: 'mine',
              src: reader.result,
              time: timeStamp,
              type: 'file',
              fileName: fileName,
            },
          ],
        }));
      }
    };
    reader.readAsDataURL(file);
    e.target.value = '';
  };

  // Open image modal
  const openImageModal = (src) => {
    setModalImage(src);
  };

  // Close image modal
  const closeImageModal = () => {
    setModalImage(null);
  };

  // Open PDF modal
  const openPdfModal = (src, fileName) => {
    setPdfModal({ open: true, src, fileName });
  };

  // Close PDF modal
  const closePdfModal = () => {
    setPdfModal({ open: false, src: '', fileName: '' });
  };

  // Chat header: clear current chat
  const handleClearChat = () => {
    if (!selectedChat) return;
    setMessages((prev) => ({
      ...prev,
      [selectedChat]: [],
    }));
    setMenuOpen(false);
  };

  // Chat header: delete entire chat
  const handleDeleteChat = () => {
    if (!selectedChat) return;
    setChatList((prevList) => prevList.filter((chat) => chat.id !== selectedChat));
    setMessages((prev) => {
      const newMessages = { ...prev };
      delete newMessages[selectedChat];
      return newMessages;
    });
    setSelectedChat(null);
    setMenuOpen(false);
  };

  // Delete a specific message from the chat
  const handleDeleteMessage = (chatId, msgIndex) => {
    setMessages((prev) => {
      const updated = [...(prev[chatId] || [])];
      updated.splice(msgIndex, 1);
      return {
        ...prev,
        [chatId]: updated,
      };
    });
    setOpenMsgOptions(null);
  };

  // Set reply for a specific message
  const handleReplyMessage = (msg) => {
    setReplyMsg(msg);
    setOpenMsgOptions(null);
  };

  // Toggle the 3-dot menu in chat header
  const toggleMenu = () => {
    setMenuOpen((prev) => !prev);
  };

  // Toggle message options dropdown (for individual messages)
  const toggleMsgOptions = (index) => {
    setOpenMsgOptions((prev) => (prev === index ? null : index));
  };

  // Close header menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="chats-container">
      <div className="chats-wrapper">
        {/* Left Panel */}
        <div className="chats-left-panel">
          <header className="chats-left-header">
            <Link to="/dashboard" className="back-link arrow-only">
              <FontAwesomeIcon icon={faArrowLeft} />
            </Link>
            <h2 className="chats-title">Chat</h2>
          </header>
          <ul className="chat-list">
            {chatList.map((chat) => (
              <li
                key={chat.id}
                className={`chat-item ${selectedChat === chat.id ? 'active' : ''}`}
                onClick={() => handleSelectChat(chat.id)}
              >
                {chat.name}
              </li>
            ))}
          </ul>
        </div>

        {/* Right Panel */}
        <div className="chats-right-panel">
          {selectedChat ? (
            <div className="chat-content">
              <header className="chats-right-header">
                <span className="chat-contact-name">
                  {chatList.find((chat) => chat.id === selectedChat)?.name}
                </span>
                <div className="chat-header-actions" ref={menuRef}>
                  <FontAwesomeIcon
                    icon={faEllipsisV}
                    className="ellipsis-icon"
                    onClick={toggleMenu}
                  />
                  {menuOpen && (
                    <div className="chat-menu-dropdown">
                      <div className="dropdown-item" onClick={handleClearChat}>
                        Clear Chat
                      </div>
                      <div className="dropdown-item" onClick={handleDeleteChat}>
                        Delete Chat
                      </div>
                    </div>
                  )}
                </div>
              </header>
              <div className="chat-messages">
                {messages[selectedChat]?.map((msg, index) => (
                  <div key={index} className={`message-bubble ${msg.sender}`}>
                    {/* Message options appear on hover */}
                    <div className="msg-options-container">
                      <FontAwesomeIcon
                        icon={faEllipsisH}
                        className="msg-options-icon"
                        onClick={() => toggleMsgOptions(index)}
                      />
                      {openMsgOptions === index && (
                        <div className="msg-options-dropdown">
                          <div
                            className="msg-option-item"
                            onClick={() => handleReplyMessage(msg)}
                          >
                            Reply
                          </div>
                          <div
                            className="msg-option-item"
                            onClick={() => handleDeleteMessage(selectedChat, index)}
                          >
                            Delete
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Render message content based on type */}
                    {msg.type === 'text' && (
                      <>
                        <div className="message-text">{msg.text}</div>
                        <div className="message-time">{msg.time}</div>
                      </>
                    )}
                    {msg.type === 'image' && (
                      <>
                        <img
                          src={msg.src}
                          alt="sent"
                          className="message-image"
                          onClick={() => openImageModal(msg.src)}
                        />
                        <div className="message-time">{msg.time}</div>
                      </>
                    )}
                    {msg.type === 'pdf' && (
                      <>
                        <div
                          className="pdf-preview"
                          onClick={() => openPdfModal(msg.src, msg.fileName)}
                        >
                          <FontAwesomeIcon icon={faFilePdf} className="pdf-icon" />
                          <span className="pdf-filename">{msg.fileName}</span>
                        </div>
                        <div className="message-time">{msg.time}</div>
                      </>
                    )}
                    {msg.type === 'file' && (
                      <>
                        <a
                          href={msg.src}
                          download={msg.fileName}
                          className="file-link"
                          title="Download File"
                        >
                          {msg.fileName}
                        </a>
                        <div className="message-time">{msg.time}</div>
                      </>
                    )}
                  </div>
                ))}
              </div>
              {/* Reply preview if a message is being replied to */}
              {replyMsg && (
                <div className="reply-preview">
                  <span className="reply-label">Replying to:</span>
                  <span className="reply-text">{replyMsg.text}</span>
                  <span className="cancel-reply" onClick={() => setReplyMsg(null)}>
                    &times;
                  </span>
                </div>
              )}
              <div className="chats-input-area">
                <input
                  type="text"
                  placeholder="Type a message..."
                  value={typedMessage}
                  onChange={(e) => setTypedMessage(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') handleSend();
                  }}
                />
                <FontAwesomeIcon
                  icon={faPaperPlane}
                  className="send-icon"
                  onClick={handleSend}
                />
                <input
                  type="file"
                  accept="image/*,.pdf,.doc,.docx,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                  ref={fileInputRef}
                  style={{ display: 'none' }}
                  onChange={handleFileChange}
                />
                <FontAwesomeIcon
                  icon={faPaperclip}
                  className="upload-icon"
                  onClick={handleUploadClick}
                />
              </div>
            </div>
          ) : (
            <div className="chat-placeholder">Start Messaging</div>
          )}
        </div>
      </div>

      {/* Image Modal */}
      {modalImage && (
        <div className="image-modal" onClick={closeImageModal}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-modal-icon" onClick={closeImageModal}>
              &times;
            </span>
            <img src={modalImage} alt="Enlarged" className="modal-image" />
            <a href={modalImage} download="image.png" className="download-link">
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}

      {/* PDF Modal */}
      {pdfModal.open && (
        <div className="pdf-modal" onClick={closePdfModal}>
          <div className="pdf-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-pdf-modal" onClick={closePdfModal}>
              &times;
            </span>
            <embed src={pdfModal.src} type="application/pdf" className="pdf-embed" />
            <a
              href={pdfModal.src}
              download={pdfModal.fileName}
              className="download-pdf-icon"
              title="Download PDF"
            >
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatListPage;
