import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faArrowLeft,
  faEllipsisV,
  faEllipsisH,
  faDownload,
  faPaperclip,
  faPaperPlane,
  faFilePdf,
  faBan,
} from '@fortawesome/free-solid-svg-icons';
import './ChatListPage.css';
import axios from 'axios'; // Make sure axios is installed

const API_BASE_URL = 'http://localhost:8000/chat/';

const ChatListPage = () => {
  // Chat state
  const [chatList, setChatList] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [messages, setMessages] = useState({});
  const [typedMessage, setTypedMessage] = useState('');
  const [replyMsg, setReplyMsg] = useState(null);
  const [isLoading, setIsLoading] = useState(true); // Add loading state
  const fileInputRef = useRef(null);

  // Modals / popups
  const [modalImage, setModalImage] = useState(null);
  const [pdfModal, setPdfModal] = useState({ open: false, src: '', fileName: '' });
  const [deletePopup, setDeletePopup] = useState({ open: false, msgIndex: null });
  const [menuOpen, setMenuOpen] = useState(false);
  const [openMsgOptions, setOpenMsgOptions] = useState(null);
  const menuRef = useRef(null);
  const msgOptionsRef = useRef(null);

  // WebSocket for the current friend
  const [socket, setSocket] = useState(null);

  const getTimeStamp = () =>
    new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  // Fetch chat list when component mounts
  useEffect(() => {
    const fetchChats = async () => {
      try {
        const token = localStorage.getItem('token'); // Assuming you store auth token in localStorage
        const response = await axios.get(`${API_BASE_URL}chats/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Transform the data to match our component's structure
        const fetchedChats = response.data.map((chat) => ({
          id: chat.id.toString(),
          name: chat.name || `Chat #${chat.id}`,
          participants: chat.participants,
        }));

        setChatList(fetchedChats);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching chats:', error);
        setIsLoading(false);
      }
    };

    fetchChats();
  }, []);

  // Fetch messages when a chat is selected
  useEffect(() => {
    if (!selectedChat) return;

    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`${API_BASE_URL}messages/?chat=${selectedChat}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Transform messages to match our component's structure
        const transformedMessages = response.data.map((msg) => {
          const messageObj = {
            id: msg.id,
            user: msg.sender.username === 'Me' ? 'Me' : msg.sender.username,
            message: msg.text,
            time: new Date(msg.created_at).toLocaleTimeString([], {
              hour: '2-digit',
              minute: '2-digit',
            }),
            type: msg.message_type,
          };

          // Handle different message types
          if (msg.message_type === 'image') {
            messageObj.src = msg.file;
          } else if (msg.message_type === 'pdf' || msg.message_type === 'file') {
            messageObj.src = msg.file;
            messageObj.fileName = msg.file.split('/').pop();
          }

          // Handle reply messages
          if (msg.reply_to) {
            messageObj.replyTo = {
              user: msg.reply_to.sender.username,
              message: msg.reply_to.text,
            };
          }

          return messageObj;
        });

        setMessages((prev) => ({
          ...prev,
          [selectedChat]: transformedMessages,
        }));
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, [selectedChat]);

  // Open WebSocket whenever selectedChat changes
  useEffect(() => {
    if (!selectedChat) return;

    // Extract the hostname from API_BASE_URL to maintain consistency
    const wsHost = API_BASE_URL.replace('http://', 'ws://').replace(/\/chat\/$/, '');
    const ws = new WebSocket(`${wsHost}/ws/chat/${selectedChat}/`);

    ws.onopen = () => {
      console.log('WebSocket connected for room:', selectedChat);
    };

    ws.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.message) {
        // Create a properly formatted message object
        const newMsg = {
          user: data.sender?.username || 'Friend',
          message: data.message,
          time: getTimeStamp(),
        };

        setMessages((prev) => ({
          ...prev,
          [selectedChat]: [...(prev[selectedChat] || []), newMsg],
        }));
      }
    };

    ws.onerror = (err) => console.error('WebSocket error:', err);
    ws.onclose = (e) => console.warn('WebSocket closed:', e);

    setSocket(ws);

    return () => ws.close();
  }, [selectedChat]);

  // Send a message
  const handleSend = async () => {
    if (!typedMessage.trim() || !selectedChat) return;

    // Create new message object
    const newMessage = {
      user: 'Me',
      message: typedMessage,
      time: getTimeStamp(),
    };

    if (replyMsg) {
      newMessage.replyTo = {
        user: replyMsg.user || 'Them',
        message: replyMsg.message,
      };
    }

    // Optimistically update UI
    setMessages((prev) => ({
      ...prev,
      [selectedChat]: [...(prev[selectedChat] || []), newMessage],
    }));

    try {
      // Send to WebSocket for real-time updates
      if (socket) {
        socket.send(JSON.stringify({ message: typedMessage }));
      }

      // Send to REST API for persistence
      const token = localStorage.getItem('token');
      const payload = {
        chat: selectedChat,
        text: typedMessage,
        message_type: 'text',
        reply_to: replyMsg?.id || null,
      };

      await axios.post(`${API_BASE_URL}messages/`, payload, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });
    } catch (error) {
      console.error('Error sending message:', error);
    }

    // Reset input
    setTypedMessage('');
    setReplyMsg(null);
  };

  // File upload
  const handleUploadClick = () => {
    if (!selectedChat) return;
    fileInputRef.current?.click();
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file || !selectedChat) return;

    const fileName = file.name;
    const fileType = file.type.toLowerCase();
    const timeStamp = getTimeStamp();

    // Create form data for file upload
    const formData = new FormData();
    formData.append('file', file);
    formData.append('chat', selectedChat);

    // Determine message type
    let messageType = 'file';
    if (fileType.startsWith('image/')) {
      messageType = 'image';
    } else if (fileType === 'application/pdf' || fileName.toLowerCase().endsWith('.pdf')) {
      messageType = 'pdf';
    }
    formData.append('message_type', messageType);

    // Create message for optimistic UI update
    const reader = new FileReader();
    reader.onload = async () => {
      let newMsg = { user: 'Me', time: timeStamp };
      if (messageType === 'image') {
        newMsg.type = 'image';
        newMsg.src = reader.result;
      } else if (messageType === 'pdf') {
        newMsg.type = 'pdf';
        newMsg.src = reader.result;
        newMsg.fileName = fileName;
      } else {
        newMsg.type = 'file';
        newMsg.src = reader.result;
        newMsg.fileName = fileName;
      }

      // Update UI optimistically
      setMessages((prev) => ({
        ...prev,
        [selectedChat]: [...(prev[selectedChat] || []), newMsg],
      }));

      // Send to backend
      try {
        const token = localStorage.getItem('token');
        await axios.post(`${API_BASE_URL}messages/`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        });
      } catch (error) {
        console.error('Error uploading file:', error);
      }
    };

    reader.readAsDataURL(file);
    e.target.value = '';
  };

  // Delete message
  const handleDeleteMessage = async (chatId, msgIndex, deleteForAll = false) => {
    const message = messages[chatId][msgIndex];

    // Optimistically update UI
    setMessages((prev) => {
      const updated = [...(prev[chatId] || [])];
      if (!deleteForAll) {
        updated.splice(msgIndex, 1);
      } else {
        const originalMsg = updated[msgIndex];
        updated[msgIndex] = {
          ...originalMsg,
          type: 'deleted',
          message: 'You deleted this message',
          replyTo: undefined,
          src: undefined,
          fileName: undefined,
        };
      }
      return { ...prev, [chatId]: updated };
    });

    try {
      const token = localStorage.getItem('token');
      if (message.id) {
        // If deleteForAll is false, we should implement "delete for me" logic
        // on the backend as well - for this example we just delete completely
        await axios.delete(`${API_BASE_URL}messages/${message.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
      }
    } catch (error) {
      console.error('Error deleting message:', error);
    }

    setDeletePopup({ open: false, msgIndex: null });
    setOpenMsgOptions(null);
  };

  // Keep the rest of the functions and UI the same
  const openImageModal = (src) => setModalImage(src);
  const closeImageModal = () => setModalImage(null);
  const openPdfModal = (src, fileName) => setPdfModal({ open: true, src, fileName });
  const closePdfModal = () => setPdfModal({ open: false, src: '', fileName: '' });
  const toggleMenu = () => setMenuOpen((prev) => !prev);
  const toggleMsgOptions = (index) => setOpenMsgOptions((prev) => (prev === index ? null : index));
  const openDeletePopup = (index) => setDeletePopup({ open: true, msgIndex: index });
  const handleDeletePopupClose = () => setDeletePopup({ open: false, msgIndex: null });

  const handleReplyMessage = (msg) => {
    setReplyMsg(msg);
    setOpenMsgOptions(null);
  };

  // Close dropdowns if clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
      if (msgOptionsRef.current && !msgOptionsRef.current.contains(e.target)) {
        setOpenMsgOptions(null);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // The return JSX stays mostly the same with loading state added
  return (
    <div className="chats-container">
      <div className="chats-wrapper">
        {/* Left Panel */}
        <div className="chats-left-panel">
          <header className="chats-left-header">
            <Link to="/dashboard" className="back-link arrow-only">
              <FontAwesomeIcon icon={faArrowLeft} />
            </Link>
            <h2 className="chats-title">Chat</h2>
          </header>

          <ul className="chat-list" style={{ textAlign: 'center' }}>
            {isLoading ? (
              <li className="chat-item">Loading chats...</li>
            ) : chatList.length === 0 ? (
              <li className="chat-item">No chats found</li>
            ) : (
              chatList.map((chat) => (
                <li
                  key={chat.id}
                  className={`chat-item ${selectedChat === chat.id ? 'active' : ''}`}
                  onClick={() => setSelectedChat(chat.id)}
                >
                  <span className="chat-link">{chat.name}</span>
                </li>
              ))
            )}
          </ul>
        </div>

        {/* Right Panel */}
        <div className={selectedChat ? 'chats-right-panel doodle' : 'chats-right-panel'}>
          {selectedChat ? (
            <div className="chat-content">
              <header className="chats-right-header">
                <span className="chat-contact-name">{selectedChat}</span>
                <div className="chat-header-actions" ref={menuRef}>
                  <FontAwesomeIcon
                    icon={faEllipsisV}
                    className="ellipsis-icon"
                    onClick={toggleMenu}
                  />
                  {menuOpen && (
                    <div className="chat-menu-dropdown">
                      <div
                        className="dropdown-item"
                        onClick={() => {
                          setMessages((prev) => ({ ...prev, [selectedChat]: [] }));
                          setMenuOpen(false);
                        }}
                      >
                        Clear Chat
                      </div>
                      <div
                        className="dropdown-item"
                        onClick={() => {
                          // Remove friend from chatList and messages
                          setChatList((prev) => prev.filter((c) => c.id !== selectedChat));
                          setMessages((prev) => {
                            const newMsgs = { ...prev };
                            delete newMsgs[selectedChat];
                            return newMsgs;
                          });
                          setSelectedChat(null);
                          setMenuOpen(false);
                        }}
                      >
                        Delete Chat
                      </div>
                    </div>
                  )}
                </div>
              </header>

              <div className="chat-messages" ref={msgOptionsRef}>
                {(messages[selectedChat] || []).map((msg, index) => {
                  const isMine = msg.user === 'Me';
                  return (
                    <div key={index} className={`message-wrapper ${isMine ? 'mine' : 'theirs'}`}>
                      {isMine && (
                        <div className="msg-options-container" onClick={(e) => e.stopPropagation()}>
                          <FontAwesomeIcon
                            icon={faEllipsisH}
                            className="msg-options-icon"
                            onClick={() => toggleMsgOptions(index)}
                          />
                          {openMsgOptions === index && (
                            <div
                              className="msg-options-dropdown"
                              onClick={(e) => e.stopPropagation()}
                            >
                              <div className="msg-option-item" onClick={() => handleReplyMessage(msg)}>
                                Reply
                              </div>
                              <div className="msg-option-item" onClick={() => openDeletePopup(index)}>
                                Delete
                              </div>
                            </div>
                          )}
                        </div>
                      )}

                      <div className={`message-bubble ${isMine ? 'mine' : 'theirs'}`}>
                        {msg.replyTo && (
                          <div className="mini-reply-bubble">
                            <span className="mini-reply-sender">{msg.replyTo.user}</span>
                            <span className="mini-reply-text">{msg.replyTo.message}</span>
                          </div>
                        )}

                        {msg.type === 'deleted' ? (
                          <>
                            <div className="message-text deleted">
                              <FontAwesomeIcon icon={faBan} className="deleted-icon" />
                              {msg.message}
                            </div>
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : msg.type === 'image' ? (
                          <>
                            <img
                              src={msg.src}
                              alt="sent"
                              className="message-image"
                              onClick={() => openImageModal(msg.src)}
                            />
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : msg.type === 'pdf' ? (
                          <>
                            <div
                              className="pdf-preview"
                              onClick={() => openPdfModal(msg.src, msg.fileName)}
                            >
                              <FontAwesomeIcon icon={faFilePdf} className="pdf-icon" />
                              <span className="pdf-filename">{msg.fileName}</span>
                            </div>
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : msg.type === 'file' ? (
                          <>
                            <a
                              href={msg.src}
                              download={msg.fileName}
                              className="file-link"
                              title="Download File"
                            >
                              {msg.fileName}
                            </a>
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : (
                          <>
                            <div className="message-text">{msg.message}</div>
                            <div className="message-time">{msg.time}</div>
                          </>
                        )}
                      </div>

                      {!isMine && (
                        <div className="msg-options-container" onClick={(e) => e.stopPropagation()}>
                          <FontAwesomeIcon
                            icon={faEllipsisH}
                            className="msg-options-icon"
                            onClick={() => toggleMsgOptions(index)}
                          />
                          {openMsgOptions === index && (
                            <div
                              className="msg-options-dropdown"
                              onClick={(e) => e.stopPropagation()}
                            >
                              <div className="msg-option-item" onClick={() => handleReplyMessage(msg)}>
                                Reply
                              </div>
                              <div className="msg-option-item" onClick={() => openDeletePopup(index)}>
                                Delete
                              </div>
                            </div>
                          )}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>

              {replyMsg && (
                <div className="reply-bubble">
                  <span className="reply-text">{replyMsg.message}</span>
                  <span className="cancel-reply" onClick={() => setReplyMsg(null)}>
                    &times;
                  </span>
                </div>
              )}

              <div className="chats-input-area">
                <input
                  type="text"
                  placeholder="Type a message..."
                  value={typedMessage}
                  onChange={(e) => setTypedMessage(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') handleSend();
                  }}
                />
                <FontAwesomeIcon icon={faPaperPlane} className="send-icon" onClick={handleSend} />
                <input
                  type="file"
                  accept="image/*,.pdf,.doc,.docx,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                  ref={fileInputRef}
                  style={{ display: 'none' }}
                  onChange={handleFileChange}
                />
                <FontAwesomeIcon icon={faPaperclip} className="upload-icon" onClick={handleUploadClick} />
              </div>
            </div>
          ) : (
            <div className="chat-placeholder">Start Messaging</div>
          )}
        </div>
      </div>

      {deletePopup.open && (
        <div className="delete-popup-modal" onClick={handleDeletePopupClose}>
          <div className="delete-popup-content" onClick={(e) => e.stopPropagation()}>
            <p>Delete message:</p>
            <div className="delete-popup-options">
              <span
                className="delete-option"
                onClick={() => handleDeleteMessage(selectedChat, deletePopup.msgIndex, false)}
              >
                Delete for me
              </span>
              <span
                className="delete-option"
                onClick={() => handleDeleteMessage(selectedChat, deletePopup.msgIndex, true)}
              >
                Delete for all
              </span>
            </div>
          </div>
        </div>
      )}

      {modalImage && (
        <div className="image-modal" onClick={closeImageModal}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-modal-icon" onClick={closeImageModal}>
              &times;
            </span>
            <img src={modalImage} alt="Enlarged" className="modal-image" />
            <a href={modalImage} download="image.png" className="download-link">
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}

      {pdfModal.open && (
        <div className="pdf-modal" onClick={closePdfModal}>
          <div className="pdf-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-pdf-modal" onClick={closePdfModal}>
              &times;
            </span>
            <embed src={pdfModal.src} type="application/pdf" className="pdf-embed" />
            <a
              href={pdfModal.src}
              download={pdfModal.fileName}
              className="download-pdf-icon"
              title="Download PDF"
            >
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatListPage;
