import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faArrowLeft,
  faEllipsisV,
  faDownload,
  faPaperclip,
  faPaperPlane,
  faFilePdf,
  faTrash,          // <-- Added trash icon
} from '@fortawesome/free-solid-svg-icons';
import './ChatListPage.css';

const initialChats = [
  { id: 1, name: 'Friend 1' },
  { id: 2, name: 'Friend 2' },
  { id: 3, name: 'Seller 1' },
];

const ChatListPage = () => {
  // Store chat list in state so we can delete a chat
  const [chatList, setChatList] = useState(initialChats);

  // Track which chat is selected
  const [selectedChat, setSelectedChat] = useState(null);

  // Example messages (with timestamps, stored by chat ID)
  const [messages, setMessages] = useState({
    1: [
      { sender: 'theirs', text: 'Hello!', time: '10:00 AM', type: 'text' },
      { sender: 'mine', text: 'Hi there!', time: '10:01 AM', type: 'text' },
    ],
    2: [],
    3: [],
  });

  // Track what the user is typing
  const [typedMessage, setTypedMessage] = useState('');

  // For file uploads (images, docs, pdf, etc.)
  const fileInputRef = useRef(null);

  // For image modal (zoomed image)
  const [modalImage, setModalImage] = useState(null);

  // For PDF modal (previewing a PDF)
  const [pdfModal, setPdfModal] = useState({ open: false, src: '', fileName: '' });

  // For the 3-dot menu
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);

  // Generate a timestamp (e.g., 10:02 AM)
  const getTimeStamp = () => {
    return new Date().toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Handle selecting a chat
  const handleSelectChat = (chatId) => {
    setSelectedChat(chatId);
    setTypedMessage('');
    setMenuOpen(false); // close menu if switching chats
  };

  // Handle sending a text message
  const handleSend = () => {
    if (!typedMessage.trim() || !selectedChat) return;
    const timeStamp = getTimeStamp();

    setMessages((prev) => ({
      ...prev,
      [selectedChat]: [
        ...(prev[selectedChat] || []),
        {
          sender: 'mine',
          text: typedMessage,
          time: timeStamp,
          type: 'text',
        },
      ],
    }));

    setTypedMessage('');
  };

  // Handle clicking the "Upload" icon
  const handleUploadClick = () => {
    if (!selectedChat) return;
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  // Handle when a file is selected
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file || !selectedChat) return;

    const timeStamp = getTimeStamp();
    const fileName = file.name;
    const fileType = file.type.toLowerCase();

    const reader = new FileReader();
    reader.onload = () => {
      // If it's an image (png, jpg, gif, etc.)
      if (fileType.startsWith('image/')) {
        setMessages((prev) => ({
          ...prev,
          [selectedChat]: [
            ...(prev[selectedChat] || []),
            {
              sender: 'mine',
              src: reader.result, // base64 data URL
              time: timeStamp,
              type: 'image',
            },
          ],
        }));
      }
      // If it's a PDF
      else if (fileType === 'application/pdf' || fileName.toLowerCase().endsWith('.pdf')) {
        setMessages((prev) => ({
          ...prev,
          [selectedChat]: [
            ...(prev[selectedChat] || []),
            {
              sender: 'mine',
              src: reader.result, // base64 data URL
              time: timeStamp,
              type: 'pdf',
              fileName: fileName,
            },
          ],
        }));
      }
      // Otherwise, treat it as a general file
      else {
        setMessages((prev) => ({
          ...prev,
          [selectedChat]: [
            ...(prev[selectedChat] || []),
            {
              sender: 'mine',
              src: reader.result,  // base64 data URL
              time: timeStamp,
              type: 'file',
              fileName: fileName,  // store the file name
            },
          ],
        }));
      }
    };
    reader.readAsDataURL(file);

    // Reset file input so the same file can be re-selected if desired
    e.target.value = '';
  };

  // Open image modal with a larger view
  const openImageModal = (src) => {
    setModalImage(src);
  };

  // Close image modal
  const closeImageModal = () => {
    setModalImage(null);
  };

  // Open PDF modal
  const openPdfModal = (src, fileName) => {
    setPdfModal({ open: true, src, fileName });
  };

  // Close PDF modal
  const closePdfModal = () => {
    setPdfModal({ open: false, src: '', fileName: '' });
  };

  // Clear all messages in the selected chat
  const handleClearChat = () => {
    if (!selectedChat) return;
    setMessages((prev) => ({
      ...prev,
      [selectedChat]: [],
    }));
    setMenuOpen(false);
  };

  // Delete the selected chat entirely
  const handleDeleteChat = () => {
    if (!selectedChat) return;
    // Remove from chatList
    setChatList((prevList) => prevList.filter((chat) => chat.id !== selectedChat));
    // Remove messages
    setMessages((prev) => {
      const newMessages = { ...prev };
      delete newMessages[selectedChat];
      return newMessages;
    });
    // Deselect chat
    setSelectedChat(null);
    setMenuOpen(false);
  };

  // **Delete a single message** from the chat
  const handleDeleteMessage = (chatId, msgIndex) => {
    setMessages((prev) => {
      const updatedChatMsgs = [...(prev[chatId] || [])];
      updatedChatMsgs.splice(msgIndex, 1); // remove that one message
      return {
        ...prev,
        [chatId]: updatedChatMsgs,
      };
    });
  };

  // Toggle the 3-dot menu
  const toggleMenu = () => {
    setMenuOpen((prev) => !prev);
  };

  // Close the menu if clicked outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="chats-container">
      <div className="chats-wrapper">
        {/* Left Panel: Chat List */}
        <div className="chats-left-panel">
          <header className="chats-left-header">
            {/* Arrow-only back button in the top-left corner */}
            <Link to="/dashboard" className="back-link arrow-only">
              <FontAwesomeIcon icon={faArrowLeft} />
            </Link>
            {/* Centered title */}
            <h2 className="chats-title">Chat</h2>
          </header>

          <ul className="chat-list">
            {chatList.map((chat) => (
              <li
                key={chat.id}
                className={`chat-item ${selectedChat === chat.id ? 'active' : ''}`}
                onClick={() => handleSelectChat(chat.id)}
              >
                {chat.name}
              </li>
            ))}
          </ul>
        </div>

        {/* Right Panel: Chat Messages */}
        <div className="chats-right-panel">
          {selectedChat ? (
            <div className="chat-content">
              <header className="chats-right-header">
                <span className="chat-contact-name">
                  {chatList.find((chat) => chat.id === selectedChat)?.name}
                </span>
                <div className="chat-header-actions" ref={menuRef}>
                  <FontAwesomeIcon
                    icon={faEllipsisV}
                    className="ellipsis-icon"
                    onClick={toggleMenu}
                  />
                  {menuOpen && (
                    <div className="chat-menu-dropdown">
                      <div className="dropdown-item" onClick={handleClearChat}>
                        Clear Chat
                      </div>
                      <div className="dropdown-item" onClick={handleDeleteChat}>
                        Delete Chat
                      </div>
                    </div>
                  )}
                </div>
              </header>
              <div className="chat-messages">
                {/* Render messages for the selected chat */}
                {messages[selectedChat]?.map((msg, index) => {
                  return (
                    <div key={index} className={`message-bubble ${msg.sender}`}>
                      {/* Delete icon in the corner of each bubble */}
                      <FontAwesomeIcon
                        icon={faTrash}
                        className="delete-message-icon"
                        title="Delete this message"
                        onClick={() => handleDeleteMessage(selectedChat, index)}
                      />

                      {/* Render message content based on type */}
                      {msg.type === 'text' && (
                        <>
                          <div className="message-text">{msg.text}</div>
                          <div className="message-time">{msg.time}</div>
                        </>
                      )}

                      {msg.type === 'image' && (
                        <>
                          <img
                            src={msg.src}
                            alt="sent"
                            className="message-image"
                            onClick={() => openImageModal(msg.src)}
                          />
                          <div className="message-time">{msg.time}</div>
                        </>
                      )}

                      {msg.type === 'pdf' && (
                        <>
                          <div
                            className="pdf-preview"
                            onClick={() => openPdfModal(msg.src, msg.fileName)}
                          >
                            <FontAwesomeIcon icon={faFilePdf} className="pdf-icon" />
                            <span className="pdf-filename">{msg.fileName}</span>
                          </div>
                          <div className="message-time">{msg.time}</div>
                        </>
                      )}

                      {msg.type === 'file' && (
                        <>
                          <a
                            href={msg.src}
                            download={msg.fileName}
                            className="file-link"
                            title="Download File"
                          >
                            {msg.fileName}
                          </a>
                          <div className="message-time">{msg.time}</div>
                        </>
                      )}
                    </div>
                  );
                })}
              </div>
              <div className="chats-input-area">
                <input
                  type="text"
                  placeholder="Type a message..."
                  value={typedMessage}
                  onChange={(e) => setTypedMessage(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') handleSend();
                  }}
                />
                {/* Use faPaperPlane from free-solid-svg-icons */}
                <FontAwesomeIcon
                  icon={faPaperPlane}
                  className="send-icon"
                  onClick={handleSend}
                />
                {/* Hidden file input for any upload */}
                <input
                  type="file"
                  accept="image/*,.pdf,.doc,.docx,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                  ref={fileInputRef}
                  style={{ display: 'none' }}
                  onChange={handleFileChange}
                />
                {/* Paperclip icon for upload */}
                <FontAwesomeIcon
                  icon={faPaperclip}
                  className="upload-icon"
                  onClick={handleUploadClick}
                />
              </div>
            </div>
          ) : (
            /* Centered placeholder message */
            <div className="chat-placeholder">Start Messaging</div>
          )}
        </div>
      </div>

      {/* Image Modal for enlarged preview */}
      {modalImage && (
        <div className="image-modal" onClick={closeImageModal}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            {/* Close icon in the top-right corner */}
            <span className="close-modal-icon" onClick={closeImageModal}>
              &times;
            </span>
            <img src={modalImage} alt="Enlarged" className="modal-image" />
            {/* Download icon link (force download attribute) */}
            <a href={modalImage} download="image.png" className="download-link">
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}

      {/* PDF Modal for previewing PDFs */}
      {pdfModal.open && (
        <div className="pdf-modal" onClick={closePdfModal}>
          <div className="pdf-modal-content" onClick={(e) => e.stopPropagation()}>
            {/* Close icon */}
            <span className="close-pdf-modal" onClick={closePdfModal}>
              &times;
            </span>
            {/* Embedded PDF (base64 data URL) */}
            <embed
              src={pdfModal.src}
              type="application/pdf"
              className="pdf-embed"
            />
            {/* Download icon link */}
            <a
              href={pdfModal.src}
              download={pdfModal.fileName}
              className="download-pdf-icon"
              title="Download PDF"
            >
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatListPage;
