import React from 'react';
import { useLocation, useParams } from 'react-router-dom';

function SubscriptionDetails() {
  const { id } = useParams();
  const location = useLocation();
  const { user, subscription } = location.state || {};

  if (!subscription) {
    return <p>Loading subscription details...</p>;
  }

  return (
    <div className="subscription-details">
      <h2>Subscription Details</h2>
      <p><strong>Name:</strong> {subscription.name}</p>
      <p><strong>Cost:</strong> Rs. {subscription.cost}</p>
      <p><strong>Billing Cycle:</strong> {subscription.reminder}</p>
      <p><strong>Sharable:</strong> {subscription.is_shareable ? 'Yes' : 'No'}</p>
      <p><strong>Auto Renew:</strong> {subscription.is_autorenew ? 'Yes' : 'No'}</p>

      <h3>User Info</h3>
      <p><strong>Username:</strong> {user?.username}</p>
      <p><strong>Email:</strong> {user?.email}</p>
    </div>
  );
}

export default SubscriptionDetails;
