import React, { useState, useRef, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faArrowLeft,
  faEllipsisV,
  faEllipsisH,
  faDownload,
  faPaperclip,
  faPaperPlane,
  faFilePdf,
  faBan,
} from '@fortawesome/free-solid-svg-icons';
import './ChatListPage.css';

const ChatListPage = () => {
  // 1) Grab the dynamic roomName from the URL: /chats/<roomName>
  const { roomName } = useParams();

  // 2) List of available friends/rooms (hard-coded for demo)
  //    In production, you might fetch this from an API.
  const [chatList, setChatList] = useState([
    { id: 'Sam', name: 'Sam' },
    { id: 'James', name: 'James' },
    { id: 'Alice', name: 'Alice' },
  ]);

  // 3) Messages for each roomName stored in a dictionary: { [roomName]: [...] }
  const [messages, setMessages] = useState({});

  // 4) Track typed message, reply, file uploads, etc.
  const [typedMessage, setTypedMessage] = useState('');
  const [replyMsg, setReplyMsg] = useState(null);
  const fileInputRef = useRef(null);

  // 5) Modals & popup states (image, PDF, delete confirmations, etc.)
  const [modalImage, setModalImage] = useState(null);
  const [pdfModal, setPdfModal] = useState({ open: false, src: '', fileName: '' });
  const [deletePopup, setDeletePopup] = useState({ open: false, msgIndex: null });
  const [menuOpen, setMenuOpen] = useState(false);

  // 6) For message-level options dropdown
  const [openMsgOptions, setOpenMsgOptions] = useState(null);
  const menuRef = useRef(null);
  const msgOptionsRef = useRef(null);

  // 7) WebSocket reference for the current room
  const [socket, setSocket] = useState(null);

  // Helper to get a simple time stamp
  const getTimeStamp = () => {
    return new Date().toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // ------------------------------------------------------------------------------
  // A) OPEN WEBSOCKET WHEN roomName CHANGES
  // ------------------------------------------------------------------------------
  useEffect(() => {
    if (!roomName) {
      // No room selected => no WebSocket
      return;
    }

    // Create the WebSocket connection for this roomName
    const ws = new WebSocket(`ws://127.0.0.1:8000/ws/chat/${roomName}/`);

    ws.onopen = () => {
      console.log('WebSocket connected for room:', roomName);
    };

    ws.onmessage = (e) => {
      const data = JSON.parse(e.data);
      // data might look like: { user: 'Sam', message: 'Hello' } or { message: 'Hello' }
      if (data.message) {
        // Append the new message object to the messages for this room
        setMessages((prev) => ({
          ...prev,
          [roomName]: [...(prev[roomName] || []), data],
        }));
      }
    };

    ws.onerror = (err) => console.error('WebSocket error:', err);
    ws.onclose = (e) => console.warn('WebSocket closed:', e);

    setSocket(ws);

    // Cleanup on unmount or room change
    return () => ws.close();
  }, [roomName]);

  // ------------------------------------------------------------------------------
  // B) SEND A MESSAGE
  // ------------------------------------------------------------------------------
  const handleSend = () => {
    if (!socket || !typedMessage.trim() || !roomName) return;

    // Build the message object that we'll store locally
    const newMessage = {
      user: 'Me', // or "mine" if you want to keep the 'mine'/'theirs' logic
      message: typedMessage,
      time: getTimeStamp(),
      // If you want to keep the older structure: { sender: 'mine', text: typedMessage, time: ... }
      // adjust as needed
    };

    // Optionally handle "reply" references
    if (replyMsg) {
      newMessage.replyTo = {
        user: replyMsg.user || 'Them',
        message: replyMsg.message,
      };
    }

    // Locally add the message
    setMessages((prev) => ({
      ...prev,
      [roomName]: [...(prev[roomName] || []), newMessage],
    }));

    // Send it over WebSocket
    socket.send(JSON.stringify({ message: typedMessage }));

    // Reset input & reply
    setTypedMessage('');
    setReplyMsg(null);
  };

  // ------------------------------------------------------------------------------
  // C) FILE UPLOAD (Images, PDFs, etc.)
  // ------------------------------------------------------------------------------
  const handleUploadClick = () => {
    if (!roomName) return;
    fileInputRef.current?.click();
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file || !roomName) return;

    const fileName = file.name;
    const fileType = file.type.toLowerCase();
    const timeStamp = getTimeStamp();

    const reader = new FileReader();
    reader.onload = () => {
      // Construct a local message object
      let newMsg = {
        user: 'Me',
        time: timeStamp,
      };
      if (fileType.startsWith('image/')) {
        newMsg.type = 'image';
        newMsg.src = reader.result;
      } else if (fileType === 'application/pdf' || fileName.toLowerCase().endsWith('.pdf')) {
        newMsg.type = 'pdf';
        newMsg.src = reader.result;
        newMsg.fileName = fileName;
      } else {
        newMsg.type = 'file';
        newMsg.src = reader.result;
        newMsg.fileName = fileName;
      }

      setMessages((prev) => ({
        ...prev,
        [roomName]: [...(prev[roomName] || []), newMsg],
      }));
    };
    reader.readAsDataURL(file);
    e.target.value = '';
  };

  // ------------------------------------------------------------------------------
  // D) OPEN/CLOSE MODALS (Images, PDFs, Delete popups, etc.)
  // ------------------------------------------------------------------------------
  const openImageModal = (src) => setModalImage(src);
  const closeImageModal = () => setModalImage(null);

  const openPdfModal = (src, fileName) => setPdfModal({ open: true, src, fileName });
  const closePdfModal = () => setPdfModal({ open: false, src: '', fileName: '' });

  const toggleMenu = () => setMenuOpen((prev) => !prev);

  // Message options (3-dot) toggles
  const toggleMsgOptions = (index) => {
    setOpenMsgOptions((prev) => (prev === index ? null : index));
  };

  const openDeletePopup = (index) => {
    setDeletePopup({ open: true, msgIndex: index });
  };

  const handleDeletePopupClose = () => {
    setDeletePopup({ open: false, msgIndex: null });
  };

  // ------------------------------------------------------------------------------
  // E) DELETE/REPLY LOGIC
  // ------------------------------------------------------------------------------
  const handleDeleteMessage = (chatId, msgIndex, deleteForAll = false) => {
    setMessages((prev) => {
      const updated = [...(prev[chatId] || [])];
      if (!deleteForAll) {
        // "Delete for me" => remove from the array
        updated.splice(msgIndex, 1);
      } else {
        // "Delete for all" => mark the message as deleted
        const originalMsg = updated[msgIndex];
        updated[msgIndex] = {
          ...originalMsg,
          type: 'deleted',
          message: 'You deleted this message',
          replyTo: undefined,
          src: undefined,
          fileName: undefined,
        };
      }
      return { ...prev, [chatId]: updated };
    });
    setDeletePopup({ open: false, msgIndex: null });
    setOpenMsgOptions(null);
  };

  const handleReplyMessage = (msg) => {
    setReplyMsg(msg);
    setOpenMsgOptions(null);
  };

  // ------------------------------------------------------------------------------
  // F) CLOSE DROPDOWNS IF CLICKING OUTSIDE
  // ------------------------------------------------------------------------------
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
      if (msgOptionsRef.current && !msgOptionsRef.current.contains(e.target)) {
        setOpenMsgOptions(null);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // ------------------------------------------------------------------------------
  // G) RENDER
  // ------------------------------------------------------------------------------
  return (
    <div className="chats-container">
      <div className="chats-wrapper">

        {/* Left Panel: List of Friends */}
        <div className="chats-left-panel">
          <header className="chats-left-header">
            <Link to="/dashboard" className="back-link arrow-only">
              <FontAwesomeIcon icon={faArrowLeft} />
            </Link>
            <h2 className="chats-title">Chat</h2>
          </header>

          <ul className="chat-list">
            {chatList.map((chat) => (
              <li
                key={chat.id}
                className={`chat-item ${roomName === chat.id ? 'active' : ''}`}
              >
                {/* Use a Link so we navigate to /chats/<chat.id> */}
                <Link to={`/chats/${chat.id}`} className="chat-link">
                  {chat.name}
                </Link>
              </li>
            ))}
          </ul>
        </div>

        {/* Right Panel: Chat content if a room is selected */}
        <div className={roomName ? 'chats-right-panel doodle' : 'chats-right-panel'}>
          {roomName ? (
            <div className="chat-content">
              <header className="chats-right-header">
                <span className="chat-contact-name">
                  {chatList.find((c) => c.id === roomName)?.name || roomName}
                </span>
                <div className="chat-header-actions" ref={menuRef}>
                  <FontAwesomeIcon
                    icon={faEllipsisV}
                    className="ellipsis-icon"
                    onClick={toggleMenu}
                  />
                  {menuOpen && (
                    <div className="chat-menu-dropdown">
                      {/* Example: "Clear Chat" or "Delete Chat" logic */}
                      <div className="dropdown-item" onClick={() => {
                        // Clear messages for this room
                        setMessages((prev) => ({ ...prev, [roomName]: [] }));
                        setMenuOpen(false);
                      }}>
                        Clear Chat
                      </div>
                      <div className="dropdown-item" onClick={() => {
                        // If you want to remove this room from chatList, etc.
                        setChatList((prev) => prev.filter((c) => c.id !== roomName));
                        setMessages((prev) => {
                          const newMsgs = { ...prev };
                          delete newMsgs[roomName];
                          return newMsgs;
                        });
                        setMenuOpen(false);
                      }}>
                        Delete Chat
                      </div>
                    </div>
                  )}
                </div>
              </header>

              {/* Messages */}
              <div className="chat-messages" ref={msgOptionsRef}>
                {(messages[roomName] || []).map((msg, index) => {
                  // If you want "mine" vs "theirs" logic, you can do:
                  const isMine = (msg.user === 'Me'); // or msg.sender === 'mine'
                  return (
                    <div
                      key={index}
                      className={`message-wrapper ${isMine ? 'mine' : 'theirs'}`}
                    >
                      {/* Three-dot message options */}
                      {isMine ? (
                        <>
                          <div
                            className="msg-options-container"
                            onClick={(e) => e.stopPropagation()}
                          >
                            <FontAwesomeIcon
                              icon={faEllipsisH}
                              className="msg-options-icon"
                              onClick={() => toggleMsgOptions(index)}
                            />
                            {openMsgOptions === index && (
                              <div
                                className="msg-options-dropdown"
                                onClick={(e) => e.stopPropagation()}
                              >
                                <div
                                  className="msg-option-item"
                                  onClick={() => handleReplyMessage(msg)}
                                >
                                  Reply
                                </div>
                                <div
                                  className="msg-option-item"
                                  onClick={() => openDeletePopup(index)}
                                >
                                  Delete
                                </div>
                              </div>
                            )}
                          </div>
                        </>
                      ) : null}

                      {/* Message bubble */}
                      <div className={`message-bubble ${isMine ? 'mine' : 'theirs'}`}>
                        {msg.replyTo && (
                          <div className="mini-reply-bubble">
                            <span className="mini-reply-sender">
                              {msg.replyTo.user}
                            </span>
                            <span className="mini-reply-text">
                              {msg.replyTo.message}
                            </span>
                          </div>
                        )}

                        {msg.type === 'deleted' ? (
                          <>
                            <div className="message-text deleted">
                              <FontAwesomeIcon icon={faBan} className="deleted-icon" />
                              {msg.message}
                            </div>
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : msg.type === 'text' || !msg.type ? (
                          <>
                            <div className="message-text">{msg.message}</div>
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : msg.type === 'image' ? (
                          <>
                            <img
                              src={msg.src}
                              alt="sent"
                              className="message-image"
                              onClick={() => openImageModal(msg.src)}
                            />
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : msg.type === 'pdf' ? (
                          <>
                            <div
                              className="pdf-preview"
                              onClick={() => openPdfModal(msg.src, msg.fileName)}
                            >
                              <FontAwesomeIcon icon={faFilePdf} className="pdf-icon" />
                              <span className="pdf-filename">{msg.fileName}</span>
                            </div>
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : msg.type === 'file' ? (
                          <>
                            <a
                              href={msg.src}
                              download={msg.fileName}
                              className="file-link"
                              title="Download File"
                            >
                              {msg.fileName}
                            </a>
                            <div className="message-time">{msg.time}</div>
                          </>
                        ) : null}
                      </div>

                      {/* For 'theirs', you might show the 3-dot options on the right side */}
                      {!isMine && (
                        <div
                          className="msg-options-container"
                          onClick={(e) => e.stopPropagation()}
                        >
                          <FontAwesomeIcon
                            icon={faEllipsisH}
                            className="msg-options-icon"
                            onClick={() => toggleMsgOptions(index)}
                          />
                          {openMsgOptions === index && (
                            <div
                              className="msg-options-dropdown"
                              onClick={(e) => e.stopPropagation()}
                            >
                              <div
                                className="msg-option-item"
                                onClick={() => handleReplyMessage(msg)}
                              >
                                Reply
                              </div>
                              <div
                                className="msg-option-item"
                                onClick={() => openDeletePopup(index)}
                              >
                                Delete
                              </div>
                            </div>
                          )}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>

              {/* If user is replying, show a bubble above input */}
              {replyMsg && (
                <div className="reply-bubble">
                  <span className="reply-text">{replyMsg.message}</span>
                  <span className="cancel-reply" onClick={() => setReplyMsg(null)}>
                    &times;
                  </span>
                </div>
              )}

              {/* Message input area */}
              <div className="chats-input-area">
                <input
                  type="text"
                  placeholder="Type a message..."
                  value={typedMessage}
                  onChange={(e) => setTypedMessage(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') handleSend();
                  }}
                />
                <FontAwesomeIcon
                  icon={faPaperPlane}
                  className="send-icon"
                  onClick={handleSend}
                />
                <input
                  type="file"
                  accept="image/*,.pdf,.doc,.docx,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                  ref={fileInputRef}
                  style={{ display: 'none' }}
                  onChange={handleFileChange}
                />
                <FontAwesomeIcon
                  icon={faPaperclip}
                  className="upload-icon"
                  onClick={handleUploadClick}
                />
              </div>
            </div>
          ) : (
            <div className="chat-placeholder">Start Messaging</div>
          )}
        </div>
      </div>

      {/* Delete Popup Modal */}
      {deletePopup.open && (
        <div
          className="delete-popup-modal"
          onClick={handleDeletePopupClose}
        >
          <div
            className="delete-popup-content"
            onClick={(e) => e.stopPropagation()}
          >
            <p>Delete message:</p>
            <div className="delete-popup-options">
              <span
                className="delete-option"
                onClick={() =>
                  handleDeleteMessage(roomName, deletePopup.msgIndex, false)
                }
              >
                Delete for me
              </span>
              <span
                className="delete-option"
                onClick={() =>
                  handleDeleteMessage(roomName, deletePopup.msgIndex, true)
                }
              >
                Delete for all
              </span>
            </div>
          </div>
        </div>
      )}

      {/* Image Modal */}
      {modalImage && (
        <div className="image-modal" onClick={closeImageModal}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-modal-icon" onClick={closeImageModal}>
              &times;
            </span>
            <img src={modalImage} alt="Enlarged" className="modal-image" />
            <a href={modalImage} download="image.png" className="download-link">
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}

      {/* PDF Modal */}
      {pdfModal.open && (
        <div className="pdf-modal" onClick={closePdfModal}>
          <div className="pdf-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-pdf-modal" onClick={closePdfModal}>
              &times;
            </span>
            <embed src={pdfModal.src} type="application/pdf" className="pdf-embed" />
            <a
              href={pdfModal.src}
              download={pdfModal.fileName}
              className="download-pdf-icon"
              title="Download PDF"
            >
              <FontAwesomeIcon icon={faDownload} />
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatListPage;
