import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './Chat.css'; 

function Chat() {
  // Get the friendId from the route params
  const { friendId } = useParams();

  // In a real app, fetch friend info using friendId. For now, use a dummy object:
  const friend = {
    id: friendId,
    name: 'Friend ' + friendId,
  };

  // Local state for messages
  const [messages, setMessages] = useState([
    { id: 1, sender: 'friend', text: `Hi, I'm ${friend.name}.` },
    { id: 2, sender: 'you', text: 'Hello! Nice to chat with you.' },
  ]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    const message = {
      id: Date.now(),
      sender: 'you',
      text: newMessage,
    };
    setMessages((prev) => [...prev, message]);
    setNewMessage('');
  };

  return (
    <div className="chat-container">
      {/* Header with a back link */}
      <header className="chat-header">
        <Link to="/chats" className="back-link">&larr; Back to Chats</Link>
        <h2>Chat with {friend.name}</h2>
      </header>
      <div className="chat-messages">
        {messages.map((msg) => (
          <div key={msg.id} className={`chat-message ${msg.sender}`}>
            <p>{msg.text}</p>
          </div>
        ))}
      </div>
      <form className="chat-input-form" onSubmit={handleSendMessage}>
        <input
          type="text"
          placeholder="Type your message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default Chat;
